<?phpsession_start();if (!isset($_SESSION['user_id'])) {  header("Location: index.html");  exit();}include 'db.php';if ($_SERVER["REQUEST_METHOD"] == "POST") {  $username = $_POST['username'];  $email = $_POST['email'];  $sql = "UPDATE users SET username='$username', email='$email' WHERE id=".$_SESSION['user_id'];  if ($conn->query($sql) === TRUE) {    $_SESSION['username'] = $username;    echo "Profile updated successfully";  } else {    echo "Error updating profile: " . $conn->error;  }  $conn->close();}?><!DOCTYPE html><html lang="en"><head>  <meta charset="UTF-8">  <meta name="viewport" content="width=device-width, initial-scale=1.0">  <title>Profile</title></head><body>  <h1>Update Profile</h1>  <form action="profile.php" method="POST">    <label for="username">Username:</label>    <input type="text" id="username" name="username" value="<?php echo $_SESSION['username']; ?>" required><br>    <label for="email">Email:</label>    <input type="email" id="email" name="email" required><br>    <input type="submit" value="Update Profile">  </form>  <a href="dashboard.php">Back to Dashboard</a></body></html>